#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="E:\project\blog\node_modules\esbuild\bin\node_modules;E:\project\blog\node_modules\esbuild\node_modules;E:\project\blog\node_modules;E:\project\node_modules;E:\node_modules;E:\project\blog\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/e/project/blog/node_modules/esbuild/bin/node_modules:/mnt/e/project/blog/node_modules/esbuild/node_modules:/mnt/e/project/blog/node_modules:/mnt/e/project/node_modules:/mnt/e/node_modules:/mnt/e/project/blog/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

# Support pipeline input
if ($MyInvocation.ExpectingInput) {
  $input | & "$basedir/../esbuild/bin/esbuild"   $args
} else {
  & "$basedir/../esbuild/bin/esbuild"   $args
}
$env:NODE_PATH=$env_node_path
exit $LASTEXITCODE
